function main(max_iterations)
{
	var tester = test_OS();
	for (var iter = 0; iter < max_iterations; iter++)
	{
		Game.time = iter;
		if (!system_boot(tester))
		{
			console.log("OS has completed all the threads. It is done")
			break;
		}
	}
}

